const fs = require('fs');
const { default : generatePalette } = require('@zimbraos/zm-x-palette-generator');

const colors = {
    "brand-primary": "#0088c1",
	"brand-secondary": "#f15b24",
	"brand-tertiary": "#0568ae",
	"brand-success": "#007a3e",
	"brand-info": "#5bc0de",
	"brand-warning": "#ffb81c",
	"brand-danger": "#cf2a2a"
}

let paletteStr = `
/* THIS FILE IS AUTOGENERATED - DO NOT MODIFY */
:root {\n`;
Object.keys(colors).forEach(index => {
    const color = colors[index];
    if (color) {
        const palette = generatePalette(color);
        Object.keys(palette).forEach(k => {
            paletteStr += `\t--${index}-${k}: ${palette[k]};\n`;
        });
    }
});

paletteStr += `}`;


fs.writeFileSync('./palette.css', paletteStr, 'UTF-8');

